func (u *UpscaleUpsertBulk) Update(set func(*UpscaleUpsert)) *UpscaleUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWith(func(update *sql.UpdateSet) {
		set(&UpscaleUpsert{UpdateSet: update})
	}))
	return u
}

